import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './QuizPage.css'; // Import CSS

function QuizPage() {
  const [questions] = useState([
    { 
      id: 1, 
      question: "What is ransomware?", 
      options: ["A computer virus", "A scam", "A type of malware that locks files", "None of the above"], 
      correct: 2 
    },
    { 
      id: 2, 
      question: "How can you prevent ransomware?", 
      options: ["Backup your data", "Disable antivirus", "Click on suspicious emails", "Ignore updates"], 
      correct: 0 
    },
    { 
      id: 3, 
      question: "What does ransomware typically do?", 
      options: ["Deletes all your files", "Encrypts your files and demands payment", "Speeds up your PC", "Creates new files"], 
      correct: 1 
    },
    { 
      id: 4, 
      question: "What should you do if you become a victim of ransomware?", 
      options: ["Pay the ransom immediately", "Disconnect from the internet and report it", "Restart your computer", "Ignore it"], 
      correct: 1 
    },
    { 
      id: 5, 
      question: "Which method do hackers use to spread ransomware?", 
      options: ["Malicious email attachments", "Phone calls", "Social media likes", "Antivirus programs"], 
      correct: 0 
    },
    { 
      id: 6, 
      question: "Which is a common type of ransomware?", 
      options: ["Trojan horse", "WannaCry", "Adware", "Spyware"], 
      correct: 1 
    },
    { 
      id: 7, 
      question: "What is the best way to recover from a ransomware attack?", 
      options: ["Pay the ransom", "Use a backup", "Wait for the hacker to remove it", "Restart your PC"], 
      correct: 1 
    },
    { 
      id: 8, 
      question: "Which file type is most dangerous when attached to an email?", 
      options: [".txt", ".exe", ".pdf", ".jpg"], 
      correct: 1 
    },
    { 
      id: 9, 
      question: "What is a good security practice to prevent ransomware?", 
      options: ["Downloading files from unknown websites", "Using strong, unique passwords", "Disabling firewall", "Sharing passwords"], 
      correct: 1 
    },
    { 
      id: 10, 
      question: "What is the main goal of ransomware?", 
      options: ["Destroying files", "Stealing credit card data", "Encrypting files and demanding ransom", "Speeding up your computer"], 
      correct: 2 
    }
  ]);

  const [answers, setAnswers] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const navigate = useNavigate();

  const handleAnswer = (answerIndex) => {
    setAnswers([...answers, answerIndex]);
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      submitQuiz();
    }
  };

  const submitQuiz = async () => {
    const score = answers.filter((answer, index) => answer === questions[index].correct).length;
    try {
      await axios.post('http://localhost:5000/api/quiz/results', { score });
      alert(`You scored ${score} out of ${questions.length}!`);
      navigate('/');
    } catch (error) {
      console.error("Error submitting quiz results", error);
    }
  };

  return (
    <div className="quiz-page">
      <h2>{questions[currentQuestion].question}</h2>
      <ul className="quiz-options">
        {questions[currentQuestion].options.map((option, index) => (
          <li key={index} onClick={() => handleAnswer(index)}>
            {option}
          </li>
        ))}
      </ul>
      <button onClick={submitQuiz}>Submit Quiz</button>
    </div>
  );
}

